import React from "react";

export default function Home() {
  return (
    <div
      className="min-h-screen bg-cover bg-center text-gray-800 p-8"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1556761175-4b46a572b786?auto=format&fit=crop&w=1470&q=80')",
      }}
    >
      <div
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.9)",
          backdropFilter: "blur(10px)",
          minHeight: "100vh",
          borderRadius: "12px",
          boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
          padding: "2rem",
          maxWidth: "1200px",
          margin: "0 auto",
        }}
      >
        <header style={{ textAlign: "center", padding: "2rem 0" }}>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "700", marginBottom: "1rem" }}>
            Alucentra Consulting
          </h1>
          <p style={{ fontSize: "1.25rem", color: "#4A5568", whiteSpace: "nowrap" }}>
            Illuminating transformation through clarity, insight, and operational excellence.
          </p>
        </header>

        <main
          style={{
            display: "grid",
            gridTemplateColumns: "1fr",
            gap: "1.5rem",
            maxWidth: "960px",
            margin: "0 auto",
          }}
        >
          {[
            {
              title: "Strategy & Transformation",
              desc: "Helping organizations navigate change with clarity and purpose.",
            },
            {
              title: "Process Design",
              desc: "Designing systems that scale, simplify, and support innovation.",
            },
            {
              title: "Change Management",
              desc: "Enabling people, teams, and culture to adapt and thrive.",
            },
          ].map(({ title, desc }) => (
            <section
              key={title}
              style={{
                backgroundColor: "#fff",
                borderRadius: "1rem",
                boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
                padding: "1rem 1.5rem",
              }}
            >
              <h2 style={{ fontSize: "1.25rem", fontWeight: "600", marginBottom: "0.5rem" }}>
                {title}
              </h2>
              <p style={{ color: "#718096" }}>{desc}</p>
            </section>
          ))}
        </main>

        <section style={{ textAlign: "center", marginTop: "4rem", maxWidth: "600px", marginLeft: "auto", marginRight: "auto" }}>
          <h3 style={{ fontSize: "1.75rem", fontWeight: "600", marginBottom: "1rem" }}>
            Ready to Illuminate Change?
          </h3>
          <p style={{ color: "#718096", marginBottom: "1.5rem" }}>
            Letâ€™s talk about how Alucentra can help you navigate complex transformation challenges.
          </p>
          <button
            style={{
              backgroundColor: "#2563EB",
              color: "white",
              borderRadius: "1rem",
              padding: "0.75rem 2rem",
              fontSize: "1.125rem",
              border: "none",
              cursor: "pointer",
            }}
          >
            Contact Us
          </button>
        </section>
      </div>
    </div>
  );
}
