export default function App() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-white to-sky-100 text-gray-800 p-8">
      {/* Hero */}

      <header className="max-w-4xl mx-auto text-center py-16">
        <h1 className="text-5xl font-extrabold mb-4">Alucentra Consulting</h1>
        <p className="text-xl text-gray-600 max-w-xl mx-auto">
    Illuminating transformation through clarity, insight, and operational excellence.
         </p>
      </header>

      {/* Services */}
      <main className="max-w-6xl mx-auto grid gap-8 md:grid-cols-3">
        {[
          {
            title: "Strategy & Transformation",
            description:
              "Helping organizations navigate change with clarity and purpose.",
          },
          {
            title: "Process Design",
            description:
              "Designing systems that scale, simplify, and support innovation.",
          },
          {
            title: "Change Management",
            description:
              "Enabling people, teams, and culture to adapt and thrive.",
          },
        ].map(({ title, description }) => (
          <section
            key={title}
            className="bg-white rounded-2xl shadow-md p-6 hover:shadow-lg transition-shadow duration-300"
          >
            <h2 className="text-2xl font-semibold mb-2">{title}</h2>
            <p className="text-gray-600">{description}</p>
          </section>
        ))}
      </main>

      {/* CTA */}
      <section className="max-w-3xl mx-auto text-center mt-20">
        <h3 className="text-3xl font-semibold mb-4">Ready to Illuminate Change?</h3>
        <p className="text-gray-700 mb-8">
          Letâ€™s talk about how Alucentra can help you navigate complex transformation challenges.
        </p>
        <button className="bg-blue-600 text-white px-8 py-3 rounded-xl text-lg hover:bg-blue-700 transition">
          Contact Us
        </button>
      </section>
    </div>
  );
}
