import React from "react";
import { Card, CardContent } from "./components/ui/Card";
import { Button } from "./components/ui/Button";
import { motion } from "framer-motion";

export default function Home() {
  return (
    <div
      className="min-h-screen bg-cover bg-center text-gray-800 p-6"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1556761175-4b46a572b786?auto=format&fit=crop&w=1470&q=80')",
      }}
    >
      <div
        className="bg-white/80 backdrop-blur-sm min-h-screen rounded-xl max-w-7xl mx-auto p-10 shadow-lg"
        style={{ backdropFilter: "blur(12px)" }}
      >
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold tracking-tight mb-4">
            Alucentra Consulting
          </h1>
          <p className="text-lg text-gray-700 max-w-xl mx-auto whitespace-nowrap">
            Illuminating transformation through clarity, insight, and operational excellence.
          </p>
        </header>

        <main className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-6xl mx-auto">
          {[
            {
              title: "Strategy & Transformation",
              desc: "Helping organizations navigate change with clarity and purpose.",
            },
            {
              title: "Process Design",
              desc: "Designing systems that scale, simplify, and support innovation.",
            },
            {
              title: "Change Management",
              desc: "Enabling people, teams, and culture to adapt and thrive.",
            },
          ].map(({ title, desc }) => (
            <motion.div key={title} whileHover={{ scale: 1.03 }}>
              <Card>
                <CardContent>
                  <h2 className="text-xl font-semibold mb-2">{title}</h2>
                  <p className="text-gray-600">{desc}</p>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </main>

        <section className="max-w-4xl mx-auto mt-16 text-center">
          <h3 className="text-2xl font-semibold mb-4">Ready to Illuminate Change?</h3>
          <p className="text-gray-600 mb-6">
            Letâ€™s talk about how Alucentra can help you navigate complex transformation challenges.
          </p>
          <Button>Contact Us</Button>
        </section>
      </div>
    </div>
  );
}
